<?php

namespace AppBundle\Repository;

/**
 * ProductRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class ProductRepository extends \Doctrine\ORM\EntityRepository
{
    public function isExist(array $options, $id): bool
    {
        $em = $this->getEntityManager();
        $productByName = $em->getRepository('AppBundle:Product')->findOneBy($options);

        if ($productByName != null) {
            if ($productByName->getId() != $id) {
                return true;
            }
        }

        return false;
    }

    public function findByPage($page = 1,
                               $items = 8,
                               $sort_by_field = 'id',
                               $order = 'asc',
                               $filter_by_field = null,
                               $pattern = null)
    {
        if ($filter_by_field == null && $pattern == null) {
            $products = $this->_em
                ->createQueryBuilder()
                ->select('p')
                ->from('AppBundle:Product', 'p')
                ->orderBy('p.' . $sort_by_field, $order)
                ->setFirstResult(($page - 1) * $items)
                ->setMaxResults($items)
                ->getQuery()
                ->getResult();
        } else {
            $products = $this->_em
                ->createQueryBuilder()
                ->select('p')
                ->from('AppBundle:Product', 'p')
                ->innerJoin('p.category', 'cat')
                ->orWhere('cat.name = :name')
                ->innerJoin('cat.parent', 'cat_p')
                ->orWhere('cat_p.name = :name')
                ->setParameter('name', $pattern)
                ->orderBy('p.' . $sort_by_field, $order)
                ->setFirstResult(($page - 1) * $items)
                ->setMaxResults($items)
                ->getQuery()
                ->getResult();
        }

        return $products;
    }

    public function countRows(
                              $filter_by_field = null,
                              $pattern = null)
    {
        if ($filter_by_field == null && $pattern == null) {
            $length = $this->_em
                ->createQueryBuilder()
                ->select('count(p.id)')
                ->from('AppBundle:Product', 'p')
                ->getQuery()->getSingleScalarResult();
        } else {
            $length = $this->_em
                ->createQueryBuilder()
                ->select('count(p.id)')
                ->from('AppBundle:Product', 'p')
                ->innerJoin('p.category', 'cat')
                ->where('cat.name = :name')
                ->innerJoin('cat.parent', 'cat_p')
                ->orWhere('cat_p.name = :name')
                ->setParameter('name', $pattern)
                ->getQuery()->getSingleScalarResult();
        }

        return $length;
    }
}
